package Code;public class Commercial extends Employe{    private float volumeMensuelDeVente;    public Commercial(String nom, String prenom, int matricule, float indiceSalaire) {        super(nom, prenom, matricule, indiceSalaire);        this.volumeMensuelDeVente = 0;    }    public void setVolumeMensuelDeVente(float volumeMensuelDeVente) {        this.volumeMensuelDeVente = volumeMensuelDeVente;    }    @Override    public String toString() {        return "Commercial{" +                "volumeMensuelDeVente=" + volumeMensuelDeVente +                ", nom='" + nom + '\'' +                ", prenom='" + prenom + '\'' +                ", matricule=" + matricule +                ", indiceSalaire=" + indiceSalaire +                '}';    }    public float CalculSalaire(){        return (float) (this.indiceSalaire*12+this.volumeMensuelDeVente*0.1);    }}