package Code;public class Commercial extends Employe{    private int volumeMensuelDeVente;    public Commercial(String nom, String prenom, int matricule, int indiceSalaire, int volumeMensuelDeVente) {        super(nom, prenom, matricule, indiceSalaire);        this.volumeMensuelDeVente = volumeMensuelDeVente;    }    @Override    public String toString() {        return "Commercial{" +                "volumeMensuelDeVente=" + volumeMensuelDeVente +                ", nom='" + nom + '\'' +                ", prenom='" + prenom + '\'' +                ", matricule=" + matricule +                ", indiceSalaire=" + indiceSalaire +                '}';    }    public  int CalculSalaire(){        return 1000;    }}