package Code;import java.util.Scanner;public class Commercial extends Employe{    private float volumeMensuelDeVente;    public Commercial(String id, String nom, String prenom, int matricule, float indiceSalaire) {        super(id, nom, prenom, matricule, indiceSalaire);        this.volumeMensuelDeVente = 0;    }    public void setVolumeMensuelDeVente(float volumeMensuelDeVente) {        this.volumeMensuelDeVente = volumeMensuelDeVente;    }    @Override    public String toString() {        return "Commercial{" +                ", nom='" + nom + '\'' +                ", prenom='" + prenom + '\'' +                ", matricule=" + matricule +                ", indiceSalaire=" + indiceSalaire +                '}';    }    public float CalculSalaire(){        Scanner sc = new Scanner(System.in);        System.out.println("Saisir le volume de vente de " +this.nom+" "+this.prenom+": ");        float volumeMensuel = sc.nextInt();        setVolumeMensuelDeVente(volumeMensuel);        return (float) (this.indiceSalaire*12+this.volumeMensuelDeVente*0.1);    }}