package Code;public class EmployeDeBase extends Employe{    public EmployeDeBase(String nom, String prenom, int matricule, int indiceSalaire) {        super(nom, prenom, matricule, indiceSalaire);    }    @Override    public String toString() {        return "EmployeDeBase{" +                "nom='" + nom + '\'' +                ", prenom='" + prenom + '\'' +                ", matricule=" + matricule +                ", indiceSalaire=" + indiceSalaire +                '}';    }    public  int CalculSalaire(){        return 1000;    }}