package Code;public class EmployeDeBase extends Employe{    public EmployeDeBase(String id, String nom, String prenom, int matricule, float indiceSalaire) {        super(id, nom, prenom, matricule, indiceSalaire);    }    @Override    public String toString() {        return "EmployeDeBase{" +                "nom='" + nom + '\'' +                ", prenom='" + prenom + '\'' +                ", matricule=" + matricule +                ", indiceSalaire=" + indiceSalaire +                '}';    }    public float CalculSalaire(){  return this.indiceSalaire*INDICE_SALAIRE;    }}