package Code;import java.io.IOException;import java.util.Scanner;public class EmployeDeBase extends Employe{    public EmployeDeBase(String id, String nom, String prenom, int matricule, float indiceSalaire) {        super(id, nom, prenom, matricule, indiceSalaire);    }    @Override    public String toString() {        return "Employe de Base: \t" +                "nom: '" + nom + '\'' +                ", prenom: '" + prenom + '\'' +                ", matricule: " + matricule +                ", indiceSalaire: " + indiceSalaire +                '}';    }    public float CalculSalaire(){  return this.indiceSalaire*INDICE_SALAIRE;    }}