package Code;import java.io.FileNotFoundException;import java.io.IOException;import java.util.Scanner;public class Main {    public static void main(String[] args) throws EntrepriseException, IOException {        Entreprise entreprise = new Entreprise();        entreprise.creationEntreprise();        menu(entreprise);    }    private static void menu(Entreprise e) {        Scanner sc = new Scanner(System.in);        boolean continuer = true;        System.out.println("\n\n\nVous pouvez désormais manipuler votre entreprise");        while(continuer) {            System.out.println("\nQue souhaitez-vous faire :\n\t" +            "Calculer le salaire de mon entreprise: tapez S\n\tAfficher la hiérarchie d'un de mes responsable: tapez H"                    + "\n\tConnaitre le matricule d'un employé : tapez M");            String res = sc.nextLine();            if (res.equals("S")) {                System.out.println(e.calculSalaire());            }            if (res.equals("H")) {                System.out.println("Saisissez le matricule de l'employe : ");                int matricule = sc.nextInt();                System.out.println(matricule);                System.out.println(e.affichageHierarchique(matricule));            }            if (res.equals("M")) {                System.out.println("Nom de l'employe");                String nom  = sc.nextLine();                System.out.println("Prenom de l'employe");                String prenom = sc.nextLine();                System.out.println(e.getMatriculeEmploye(nom, prenom));            }            if (res.equals("Q")) {                continuer = false;            }        }    }}