package Code;import java.util.ArrayList;import java.util.TreeSet;public class Responsable extends Employe {    private TreeSet<Employe> lesEmployes;    public Responsable(String nom, String prenom, int matricule, float indiceSalaire) {        super(nom, prenom, matricule, indiceSalaire);        this.lesEmployes = new TreeSet<>();    }    public void setLesEmployes(TreeSet<Employe> lesEmployes) {        this.lesEmployes = lesEmployes;    }    @Override    public String toString() {        return "Responsable{" +                "lesEmployes=" + lesEmployes +                '}';    }    public float CalculSalaire(){        return this.indiceSalaire*12;    }    /*public String afficherHierarchieInf(){    }    */}