package Code;import java.io.IOException;import java.util.ArrayList;import java.util.HashSet;import java.util.Scanner;import java.util.TreeSet;public class Responsable extends Employe {    protected HashSet<Employe> EmployeSousResp;    public Responsable(String id, String nom, String prenom, int matricule, float indiceSalaire) {        super(id, nom, prenom, matricule, indiceSalaire);        this.EmployeSousResp = new HashSet<>();    }    public void setLesEmployes(HashSet<Employe> lesEmployes) {        this.EmployeSousResp = lesEmployes;    }    public void addEmployee(Employe e){        EmployeSousResp.add(e);    }    @Override    public String toString() {        return "\nResponsable "+id+" : \t" +                "nom:'" + nom + '\'' +                ", prenom:'" + prenom + '\'' +                ", matricule:" + matricule +                ", indiceSalaire:" + getIndiceSalaire() ;    }    public float CalculSalaire(){        return this.indiceSalaire*INDICE_SALAIRE;    }    public String afficherHierarchieInf(){        String aff = "Responsable "+id+" : \t" +                "nom:'" + nom + '\'' +                ", prenom:'" + prenom + '\'' +                ", matricule:" + matricule +                ", indiceSalaire:" + getIndiceSalaire();        for(Employe e: EmployeSousResp){            if(e.id.length() <= 4){                Responsable r = (Responsable) e;                String aff1 = r.afficherHierarchieInf();                aff += aff1;            }            else {                aff += "\t" + e.toString();            }        }        return aff;    }}