package Code;import java.util.ArrayList;import java.util.HashSet;import java.util.TreeSet;public class Responsable extends Employe {    protected HashSet<Employe> EmployeSousResp;    public Responsable(String id, String nom, String prenom, int matricule, float indiceSalaire) {        super(id, nom, prenom, matricule, indiceSalaire);        this.EmployeSousResp = new HashSet<>();    }    public void setLesEmployes(HashSet<Employe> lesEmployes) {        this.EmployeSousResp = lesEmployes;    }    @Override    public String toString() {        return "Responsable{" +                "nom='" + nom + '\'' +                ", prenom='" + prenom + '\'' +                ", matricule=" + matricule +                ", indiceSalaire=" + indiceSalaire +                '}';    }    public float CalculSalaire(){        return this.indiceSalaire*INDICE_SALAIRE;    }    /*public String afficherHierarchieInf(){    }    */}